/*	libmikmodversion.r	NOTE: this file needs macintosh linefeeds (\r) to work with Rez!*/#ifndef __TYPES_R__#include "Types.r"#endif#ifndef __BALLOONS_R__#include "Balloons.r"#endif#define VERSION_MAJOR 		1#define VERSION_MINOR 		0#define REVISION     			0#define STATE	    			    release		/* development | alpha | beta | release */#define RELEASE_NO                0	  /* number after letter, or zero for release */#define COUNTRY				        verUS#define VERSION_STRING 		  "1.0.0"#define NAME					      "SDL_sound"#define SHORT_DESCRIPTION		"SDL_sound based on SDL technology"#define LONG_DESCRIPTION		"A cross-platform audio decoding library."resource 'vers' (1) {	VERSION_MAJOR,	(VERSION_MINOR << 4) | REVISION,	STATE,	RELEASE_NO,	COUNTRY,	VERSION_STRING,	VERSION_STRING};resource 'vers' (2) {	VERSION_MAJOR,	(VERSION_MINOR << 4) | REVISION,	STATE,	RELEASE_NO,	COUNTRY,	VERSION_STRING,  SHORT_DESCRIPTION};/* Extension Manager info */data 'CCIª' (128) {			NAME "\n\n"		LONG_DESCRIPTION};/* Finder help balloon */resource 'hfdr' (kHMHelpID) {	HelpMgrVersion,	hmDefaultOptions,	0,	0,	{			HMStringItem		{			NAME "\n\n"				LONG_DESCRIPTION		}	}	};/* Owner resource */data 'lmm!' (0) {	$"00"                                                 };